!(function () {
  "use strict";
  if (!window.AltiusChatWidget) {
    // Configuration
    const CONFIG = {
      API_BASE_URL:
        "https://aionegml-dev-c3aqcqg7abhhb2ag.southeastasia-01.azurewebsites.net",
      CHAT_ENDPOINT: "/chat",
      SESSION_ENDPOINT: "/create_session",
      WIDGET_ID: "chat-widget",
      CONTAINER_WIDTH: "350px",
      CONTAINER_HEIGHT: "500px",
    };

    // Global state variables
    let chatToggle, chatContainer, chatMessages, chatInput, chatSend;
    let isChatOpen = false;
    let isProcessing = false;
    let currentSessionId = null;
    let chatHistory = [];
    let widgetInitTime = new Date();

    // User agent data
    let userAgentData = {
      useragent_name: "Altius People",
      bot_name: "Altius Assistant",
      bot_logo_url:
        "data:image/png;base64,",
    };

    // Fetch session ID from API
    async function fetchSessionId() {
      const token = window.chat_api_key;
      if (!token) {
        throw new Error("No authentication token available");
      }
      try {
        const response = await fetch(
          CONFIG.API_BASE_URL + CONFIG.SESSION_ENDPOINT,
          {
            method: "GET",
            headers: {
              "qubisa-token-key": token,
            },
          }
        );
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        currentSessionId = data.session_id || null;
        console.log("Session initialized:", currentSessionId);
      } catch (error) {
        console.error("Error fetching session_id:", error);
        currentSessionId = null;
      }
    }

    // Fetch user agent details from API
    async function fetchUserAgentDetails() {
      const token = window.chat_api_key;
      const api_tenant = window.chat_api_tenant;
      if (!token) {
        console.warn(
          "No authentication token available for user agent details"
        );
        return;
      }

      try {
        const response = await fetch(
          `${CONFIG.API_BASE_URL}/detail_useragent/${token}`,
          {
            method: "GET",
            headers: {
              "qubisa-api-key": api_tenant,
            },
          }
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log("agent detail", data.data.detail_collection[0].bot_name);

        if (
          data.success &&
          data.data &&
          data.data.detail_collection &&
          data.data.detail_collection.length > 0
        ) {
          const agentDetail = data.data.detail_collection[0];
          userAgentData = {
            useragent_name: agentDetail.useragent_name || "Altius People",
            bot_name: agentDetail.bot_name,
            bot_logo_url:
              agentDetail.bot_logo_url || userAgentData.bot_logo_url,
          };

          // Update the header elements if they exist
          updateHeaderElements();
        }
      } catch (error) {
        console.error("Error fetching user agent details:", error);
      }
    }

    // Update header elements with user agent data
    function updateHeaderElements() {
      const titleElement = document.querySelector(".chat-title");
      if (titleElement) {
        titleElement.textContent = userAgentData.useragent_name;
      }
    }

    // Main initialization
    function initializeChatWidget() {
      if (document.readyState === "loading") {
        document.addEventListener("DOMContentLoaded", setupWidget);
      } else {
        setupWidget();
      }
    }

    // Setup widget components
    function setupWidget() {
      // Validate required variables
      if (!validateRequiredVariables()) {
        return;
      }

      // Create and append widget
      const widgetElement = createWidgetElement();
      document.body.appendChild(widgetElement);

      // Initialize DOM references
      initializeDOMElements();

      // Disable input and send button until session is ready
      chatInput.disabled = true;
      chatSend.disabled = true;

      // Initialize chat history
      chatHistory = [
        {
          role: "assistant",
          content: `Halo! Saya ${userAgentData.bot_name}. Ada yang bisa saya bantu?`,
        },
      ];

      // Fetch session_id and user agent details, then enable input
      Promise.all([fetchSessionId(), fetchUserAgentDetails()]).then(() => {
        chatInput.disabled = false;
        chatSend.disabled = !chatInput.value.trim();
        // Render welcome message after agent data is ready
        renderMessage(
          `Halo! Saya adalah asisten virtual AI anda. ada yang bisa saya bantu hari ini ?`,
          false,
          widgetInitTime
        );
      });

      // Setup event listeners
      setupEventListeners();
    }

    // Validate required global variables
    function validateRequiredVariables() {
      if (
        !window.chat_api_key ||
        !window.personal_data ||
        !window.chat_api_tenant
      ) {
        console.error("Altius Chat Widget: Required variables missing");
        return false;
      }
      return true;
    }

    // Create main widget element
    function createWidgetElement() {
      const widget = document.createElement("div");
      widget.id = CONFIG.WIDGET_ID;
      widget.innerHTML = generateWidgetHTML();
      injectStyles();
      return widget;
    }

    // Generate widget HTML structure
    function generateWidgetHTML() {
      return `
            <div id="chat-toggle" class="chat-toggle">
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M20 2H4C2.9 2 2 2.9 2 4V22L6 18H20C21.1 18 22 17.1 22 16V4C22 2.9 21.1 2 20 2ZM20 16H5.17L4 17.17V4H20V16Z" fill="white"/>
                    <path d="M7 9H17V11H7V9ZM7 12H15V14H7V12Z" fill="white"/>
                </svg>
                <span class="chat-badge">1</span>
            </div>
            <div id="chat-container" class="chat-container">
                <div class="chat-header">
                    <div class="chat-header-info">
                        <div class="chat-title">${userAgentData.useragent_name}</div>
                        <div class="chat-status">Online</div>
                    </div>
                    <button id="chat-close" class="chat-close">
                        <svg width="18" height="18" viewBox="0 0 18 18" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M14.25 4.81L13.19 3.75L9 7.94L4.81 3.75L3.75 4.81L7.94 9L3.75 13.19L4.81 14.25L9 10.06L13.19 14.25L14.25 13.19L10.06 9L14.25 4.81Z" fill="white"/>
                        </svg>
                    </button>
                </div>
                <div id="chat-messages" class="chat-messages">
                    <!-- Messages will be rendered dynamically -->
                </div>
                <div class="chat-input-container">
                    <div class="chat-input-wrapper">
                        <input type="text" id="chat-input" class="chat-input" placeholder="Ketik pesan Anda..." maxlength="500">
                        <button id="chat-send" class="chat-send">
                            <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                                <path d="M2.5 10L17.5 2.5L15 10L17.5 17.5L2.5 10Z" fill="white"/>
                            </svg>
                        </button>
                    </div>
                </div>
            </div>
      `;
    }

    // Initialize DOM element references
    function initializeDOMElements() {
      chatToggle = document.getElementById("chat-toggle");
      chatContainer = document.getElementById("chat-container");
      chatMessages = document.getElementById("chat-messages");
      chatInput = document.getElementById("chat-input");
      chatSend = document.getElementById("chat-send");
    }

    // Setup all event listeners
    function setupEventListeners() {
      // Toggle chat visibility
      chatToggle.addEventListener("click", toggleChat);

      // Close chat
      document
        .getElementById("chat-close")
        .addEventListener("click", closeChat);

      // Send message
      chatSend.addEventListener("click", sendMessage);

      // Enter key to send message
      chatInput.addEventListener("keypress", (event) => {
        if (event.key === "Enter" && !event.shiftKey) {
          event.preventDefault();
          sendMessage();
        }
      });

      // Enable/disable send button based on input content
      chatInput.addEventListener("input", () => {
        chatSend.disabled = !chatInput.value.trim();
      });

      // Close chat when clicking outside
      document.addEventListener("click", (event) => {
        if (
          isChatOpen &&
          !document.getElementById(CONFIG.WIDGET_ID).contains(event.target)
        ) {
          closeChat();
        }
      });

      // Prevent chat from closing when clicking inside
      chatContainer.addEventListener("click", (event) => {
        event.stopPropagation();
      });
    }

    // Chat visibility controls
    function toggleChat() {
      if (isChatOpen) {
        closeChat();
      } else {
        openChat();
      }
    }

    function openChat() {
      isChatOpen = true;
      chatContainer.classList.add("open");
      document.querySelector(".chat-badge").style.display = "none";
      chatInput.focus();
    }

    function closeChat() {
      isChatOpen = false;
      chatContainer.classList.remove("open");
    }

    // Render message dynamically
    function renderMessage(text, isUser = false, customTime = null) {
      const messageElement = document.createElement("div");
      messageElement.className = `message ${
        isUser ? "user-message" : "bot-message"
      }`;

      // Reset animation for new message
      messageElement.style.animation = "none";
      messageElement.offsetHeight; // Trigger reflow
      messageElement.style.animation = null;

      const formattedText = formatMessageText(text);
      const now = customTime ? customTime : new Date();
      const timeString = now.toLocaleTimeString("id-ID", {
        hour: "2-digit",
        minute: "2-digit",
      });

      messageElement.innerHTML = `
        <div class="message-content">
          <div class="message-text">${formattedText}</div>
          <div class="message-time">${timeString}</div>
        </div>
      `;

      chatMessages.appendChild(messageElement);
      scrollToBottom();
      return messageElement;
    }

    // Show typing indicator
    function showTypingIndicator() {
      const typingElement = document.createElement("div");
      typingElement.className = "message bot-message";
      typingElement.id = "typing-indicator";
      typingElement.innerHTML = `
        <div class="message-content">
          <div class="message-text">Sedang mengetik<span class="loading-dots"></span></div>
        </div>
      `;
      chatMessages.appendChild(typingElement);
      scrollToBottom();
      return typingElement;
    }

    // Remove typing indicator
    function removeTypingIndicator() {
      const typingElement = document.getElementById("typing-indicator");
      if (typingElement) {
        typingElement.remove();
      }
    }

    // API communication
    async function sendChatMessage(prompt) {
      try {
        // Add user message to history
        chatHistory.push({
          role: "user",
          content: prompt,
        });

        const requestBody = {
          prompt: prompt,
          user_profile: window.personal_data,
          session_id: currentSessionId,
          chat_history: chatHistory.filter((msg) => msg.role !== "system"),
        };

        const response = await fetch(
          CONFIG.API_BASE_URL + CONFIG.CHAT_ENDPOINT,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              "qubisa-token-key": window.chat_api_key,
            },
            body: JSON.stringify(requestBody),
          }
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log("API Response:", data);

        // Update session ID if provided
        if (data.session_id) {
          currentSessionId = data.session_id;
        }

        return data.response || "Maaf, terjadi kesalahan.";
      } catch (error) {
        console.error("Error sending message to API:", error);
        throw error;
      }
    }

    // Message handling
    async function sendMessage() {
      const messageText = chatInput.value.trim();

      if (!messageText || isProcessing) {
        return;
      }

      // Add user message
      renderMessage(messageText, true);
      chatInput.value = "";
      chatSend.disabled = true;

      // Show typing indicator
      setProcessingState(true);
      const typingIndicator = showTypingIndicator();

      try {
        // Send message to API
        const response = await sendChatMessage(messageText);

        // Remove typing indicator
        removeTypingIndicator();

        // Add bot response to history
        chatHistory.push({
          role: "assistant",
          content: response,
        });

        // Add bot response
        renderMessage(response, false);
      } catch (error) {
        console.error("Error in sendMessage:", error);
        removeTypingIndicator();
        renderMessage(
          "Maaf, terjadi kesalahan koneksi. Silakan coba lagi.",
          false
        );
      } finally {
        setProcessingState(false);
      }
    }

    // Text formatting utility
    function formatMessageText(text) {
      if (!text) return "";

      return (
        text
          // Convert \n to <br> for line breaks
          .replace(/\\n/g, "<br>")
          // Convert actual newlines to <br>
          .replace(/\n/g, "<br>")
          // Convert multiple spaces to non-breaking spaces
          .replace(/ {2,}/g, function (match) {
            return "&nbsp;".repeat(match.length);
          })
          // Convert numbered lists (1. 2. 3. etc.)
          .replace(/(\d+\.\s)/g, "<strong>$1</strong>")
          // Convert bullet points
          .replace(/^[-•]\s/gm, "• ")
          // Make important text bold
          .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>")
          // Make italic text
          .replace(/\*(.*?)\*/g, "<em>$1</em>")
          // Convert code blocks
          .replace(/`(.*?)`/g, "<code>$1</code>")
      );
    }

    // Utility functions
    function setProcessingState(processing) {
      isProcessing = processing;
      chatSend.disabled = processing;
      chatInput.disabled = processing;
    }

    function scrollToBottom() {
      chatMessages.scrollTop = chatMessages.scrollHeight;
    }

    // Inject CSS styles
    function injectStyles() {
      const styleElement = document.createElement("style");
      styleElement.textContent = generateStyles();
      document.head.appendChild(styleElement);
    }

    // Generate CSS styles
    function generateStyles() {
      return `
            #chat-widget {
                position: fixed;
                bottom: 20px;
                right: 20px;
                z-index: 10000;
                font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
            }
            
            .chat-toggle {
                width: 60px;
                height: 60px;
                background: #7fd8c2;
                border-radius: 50%;
                display: flex;
                align-items: center;
                justify-content: center;
                cursor: pointer;
                box-shadow: 0 4px 20px rgba(127, 216, 194, 0.3);
                transition: all 0.3s ease;
                position: relative;
            }
            
            .chat-toggle:hover {
                transform: scale(1.1);
                box-shadow: 0 6px 25px rgba(127, 216, 194, 0.4);
            }
            
            .chat-badge {
                position: absolute;
                top: -5px;
                right: -5px;
                background: #4f8ca5;
                color: white;
                border-radius: 50%;
                width: 20px;
                height: 20px;
                display: flex;
                align-items: center;
                justify-content: center;
                font-size: 12px;
                font-weight: bold;
            }
            
            .chat-container {
                position: absolute;
                bottom: 80px;
                right: 0;
          width: ${CONFIG.CONTAINER_WIDTH};
          height: ${CONFIG.CONTAINER_HEIGHT};
                background: #f8fafc;
                border-radius: 12px;
                box-shadow: 0 10px 40px rgba(127, 216, 194, 0.15);
                display: none;
                flex-direction: column;
                overflow: hidden;
                animation: slideUp 0.3s ease;
            }
            
            @keyframes slideUp {
                from {
                    opacity: 0;
                    transform: translateY(20px);
                }
                to {
                    opacity: 1;
                    transform: translateY(0);
                }
            }
            
            .chat-container.open {
                display: flex;
            }
            
            .chat-header {
                background: #7fd8c2;
                color: #23404a;
                padding: 16px 20px;
                display: flex;
                align-items: center;
                justify-content: space-between;
            }
            
            .chat-header-info {
                flex: 1;
            }
            
            .chat-title {
                font-weight: 600;
                font-size: 16px;
                margin-bottom: 2px;
            }
            
            .chat-status {
                font-size: 12px;
                opacity: 0.8;
            }
            
            .chat-close {
                background: none;
                border: none;
                color: #23404a;
                cursor: pointer;
                padding: 4px;
                border-radius: 4px;
                transition: background 0.2s ease;
            }
            
            .chat-close:hover {
                background: rgba(79, 140, 165, 0.1);
            }
            
            .chat-messages {
                flex: 1;
                overflow-y: auto;
                padding: 16px;
                background: #ffffff;
            }
            
            .chat-messages::-webkit-scrollbar {
                width: 4px;
            }
            
            .chat-messages::-webkit-scrollbar-track {
                background: transparent;
            }
            
            .chat-messages::-webkit-scrollbar-thumb {
                background: #d2f1e6;
                border-radius: 2px;
            }
            
            .message {
                margin-bottom: 16px;
                display: flex;
                align-items: flex-end;
            }
            
            .user-message {
                justify-content: flex-end;
            }
            
            .message-content {
                max-width: 80%;
                position: relative;
            }
            
            .message-text {
                padding: 12px 16px;
                border-radius: 18px;
                font-size: 14px;
                line-height: 1.4;
                word-wrap: break-word;
            }
            
            .message-text strong {
                font-weight: 600;
                color: inherit;
            }
            
            .message-text em {
                font-style: italic;
                color: inherit;
            }
            
            .message-text code {
                background: rgba(127, 216, 194, 0.1);
                padding: 2px 4px;
                border-radius: 3px;
                font-family: 'Courier New', monospace;
                font-size: 12px;
            }
            
            .message-text br {
                margin-bottom: 4px;
            }
            
            .bot-message .message-text {
                background: #f0f9f6;
                color: #23404a;
                border-bottom-left-radius: 4px;
                box-shadow: 0 1px 2px rgba(127, 216, 194, 0.1);
            }
            
            .user-message .message-text {
                background: #7fd8c2;
                color: #23404a;
                border-bottom-right-radius: 4px;
            }
            
            .message-time {
                font-size: 11px;
                color: #4f8ca5;
                margin-top: 4px;
                text-align: right;
            }
            
            .user-message .message-time {
                text-align: left;
            }
            
            .chat-input-container {
                padding: 16px;
                background: #f8fafc;
                border-top: 1px solid #e8f5f2;
            }
            
            .chat-input-wrapper {
                display: flex;
                align-items: center;
                background: #ffffff;
                border-radius: 25px;
                padding: 4px;
                border: 1px solid #e8f5f2;
            }
            
            .chat-input {
                flex: 1;
                border: none;
                background: none;
                padding: 12px 16px;
                font-size: 14px;
                outline: none;
                border-radius: 25px;
            }
            
            .chat-input::placeholder {
                color: #4f8ca5;
                opacity: 0.6;
            }
            
            .chat-send {
                width: 40px;
                height: 40px;
                background: #7fd8c2;
                border: none;
                border-radius: 50%;
                display: flex;
                align-items: center;
                justify-content: center;
                cursor: pointer;
                transition: all 0.2s ease;
            }
            
            .chat-send:hover:not(:disabled) {
                transform: scale(1.05);
                background: #6bc4b0;
            }
            
            .chat-send:disabled {
                opacity: 0.6;
                cursor: not-allowed;
            }
            
            .loading-dots {
                display: inline-block;
            }
            
            .loading-dots::after {
                content: '';
                animation: dots 1.5s infinite;
            }
            
            @keyframes dots {
                0%, 20% { content: ''; }
                40% { content: '.'; }
                60% { content: '..'; }
                80%, 100% { content: '...'; }
            }
            
            @media (max-width: 480px) {
                .chat-container {
                    width: calc(100vw - 40px);
                    height: calc(100vh - 120px);
                    bottom: 80px;
                    right: 20px;
                }
            }
      `;
    }

    // Start the widget
    initializeChatWidget();
  }
})();
